import React, { useEffect, useState } from "react";
import { getFoods } from "./api";
import "./App.css";
import { useCart } from "./CartContext";
import CartPage from "./CartPage";

function App() {
  const [foods, setFoods] = useState([]);
  const [filter, setFilter] = useState("T·∫•t c·∫£");
  const [showCart, setShowCart] = useState(false);
  const { addToCart } = useCart();
  const [quantities, setQuantities] = useState({});

  useEffect(() => {
    async function fetchData() {
      const data = await getFoods();
      setFoods(data);
    }
    fetchData();
  }, []);

  const categories = ["T·∫•t c·∫£", ...new Set(foods.map((f) => f.category || "Kh√°c"))];
  const filteredFoods =
    filter === "T·∫•t c·∫£" ? foods : foods.filter((f) => f.category === filter);

  // C·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng m√≥n
  const updateQuantity = (id, value) => {
    if (value < 1) value = 1;
    setQuantities({ ...quantities, [id]: value });
  };

  return (
    <div className="container">
      <h1>üçΩÔ∏è Foodie Express Menu</h1>

      {/* N√∫t chuy·ªÉn qua l·∫°i gi·ªØa menu v√† gi·ªè h√†ng */}
      <div style={{ textAlign: "right", marginBottom: "10px" }}>
        <button className="cart-btn" onClick={() => setShowCart(!showCart)}>
          {showCart ? "‚¨ÖÔ∏è Quay l·∫°i menu" : "üõí Xem gi·ªè h√†ng"}
        </button>
      </div>

      {showCart ? (
        <CartPage />
      ) : (
        <>
          {/* B·ªô l·ªçc theo danh m·ª•c */}
          <div className="filter">
            {categories.map((cat) => (
              <button
                key={cat}
                className={filter === cat ? "active" : ""}
                onClick={() => setFilter(cat)}
              >
                {cat}
              </button>
            ))}
          </div>

          {/* Danh s√°ch m√≥n ƒÉn */}
          {foods.length === 0 ? (
            <p>Loading...</p>
          ) : (
            <div className="grid">
              {filteredFoods.map((food) => (
                <div key={food._id} className="card">
                  <img
                    src={food.image || "https://via.placeholder.com/200x150?text=No+Image"}
                    alt={food.name}
                  />
                  <h3>{food.name}</h3>
                  <p className="price">{food.price.toLocaleString()}‚Ç´</p>
                  <p className="category">{food.category}</p>

                  {/* Ch·ªçn s·ªë l∆∞·ª£ng */}
                  <div className="quantity-control">
                    <button
                      onClick={() =>
                        updateQuantity(food._id, (quantities[food._id] || 1) - 1)
                      }
                    >
                      -
                    </button>
                    <input
                      type="number"
                      min="1"
                      value={quantities[food._id] || 1}
                      onChange={(e) =>
                        updateQuantity(food._id, parseInt(e.target.value) || 1)
                      }
                    />
                    <button
                      onClick={() =>
                        updateQuantity(food._id, (quantities[food._id] || 1) + 1)
                      }
                    >
                      +
                    </button>
                  </div>

                  {/* N√∫t th√™m v√†o gi·ªè h√†ng */}
                  <button
                    className="buy-btn"
                    onClick={() =>
                      addToCart({ ...food, quantity: quantities[food._id] || 1 })
                    }
                  >
                    üõç Th√™m v√†o gi·ªè
                  </button>
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
