// CartPage.js
import React from "react";
import { useCart } from "./CartContext";

export default function CartPage() {
  const { cart, increaseQuantity, decreaseQuantity, updateQuantity, removeFromCart, getTotalPrice, clearCart } = useCart();

  if (!cart || cart.length === 0) {
    return (
      <div style={{ padding: 20 }}>
        <h3>Gi·ªè h√†ng tr·ªëng</h3>
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>üõí Gi·ªè h√†ng c·ªßa b·∫°n</h2>

      <div style={{ marginTop: 12 }}>
        {cart.map((item) => (
          <div
            key={item._id}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              padding: "8px 0",
              borderBottom: "1px solid #eee",
            }}
          >
            <div>
              <div style={{ fontWeight: 700 }}>{item.name}</div>
              <div style={{ color: "#777" }}>
                {item.price.toLocaleString()}‚Ç´
              </div>
            </div>

            <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
              <button
                onClick={() => decreaseQuantity(item._id)}
                style={{
                  padding: "4px 8px",
                  borderRadius: 4,
                  border: "1px solid #ccc",
                }}
              >
                -
              </button>

              {/* üî¢ Input nh·∫≠p s·ªë l∆∞·ª£ng tr·ª±c ti·∫øp */}
              <input
                type="number"
                min="1"
                value={item.quantity}
                onChange={(e) => updateQuantity(item._id, e.target.value)}
                style={{
                  width: 50,
                  textAlign: "center",
                  border: "1px solid #ccc",
                  borderRadius: 4,
                  padding: "2px 4px",
                }}
              />

              <button
                onClick={() => increaseQuantity(item._id)}
                style={{
                  padding: "4px 8px",
                  borderRadius: 4,
                  border: "1px solid #ccc",
                }}
              >
                +
              </button>

              <div style={{ width: 16 }} />

              <div style={{ fontWeight: 700 }}>
                {(item.price * item.quantity).toLocaleString()}‚Ç´
              </div>

              <button
                onClick={() => removeFromCart(item._id)}
                style={{
                  marginLeft: 12,
                  background: "transparent",
                  border: "none",
                  color: "#c00",
                  cursor: "pointer",
                  fontSize: 18,
                }}
              >
                ‚ùå
              </button>
            </div>
          </div>
        ))}
      </div>

      <h3 style={{ marginTop: 16 }}>
        T·ªïng c·ªông: {getTotalPrice().toLocaleString()}‚Ç´
      </h3>

      <div style={{ marginTop: 12 }}>
        <button
          onClick={clearCart}
          style={{
            marginRight: 8,
            padding: "8px 12px",
            borderRadius: 6,
            border: "1px solid #ccc",
            background: "#f8f8f8",
          }}
        >
          üßπ X√≥a gi·ªè
        </button>
        <button
          style={{
            padding: "8px 12px",
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: 6,
          }}
        >
          ‚úÖ Thanh to√°n
        </button>
      </div>
    </div>
  );
}
