// App.js
import React, { useEffect, useState } from "react";
import { getFoods } from "./api";
import "./App.css";
import FoodItem from "./FoodItem";
import CartPage from "./CartPage";
import { useCart } from "./CartContext";
import { useAuth } from "./AuthContext";
import { ToastContainer } from "react-toastify";
import LoginPage from "./LoginPage";
import RegisterPage from "./RegisterPage";

function App() {
  const [foods, setFoods] = useState([]);
  const [filter, setFilter] = useState("T·∫•t c·∫£");
  const [showCart, setShowCart] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const { cart } = useCart();
  const { user, logout } = useAuth();

  // L·∫•y danh s√°ch m√≥n ƒÉn
  useEffect(() => {
    async function fetchData() {
      const data = await getFoods();
      setFoods(data || []);
    }
    fetchData();
  }, []);

  const categories = ["T·∫•t c·∫£", ...new Set(foods.map((f) => f.category || "Kh√°c"))];
  const filtered = filter === "T·∫•t c·∫£" ? foods : foods.filter((f) => f.category === filter);

  return (
    <div className="container">
      <ToastContainer position="top-right" autoClose={1800} />

      {/* Header */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <h1>üçΩÔ∏è Foodie Express Menu</h1>

        <div style={{ display: "flex", gap: 10 }}>
          {!user && (
            <>
              <button onClick={() => setShowLogin(true)}>ƒêƒÉng nh·∫≠p</button>
              <button onClick={() => setShowRegister(true)}>ƒêƒÉng k√Ω</button>
            </>
          )}
          {user && (
            <>
              <span>üëã Xin ch√†o, {user.name || user.email}</span>
              <button onClick={logout}>ƒêƒÉng xu·∫•t</button>
            </>
          )}

          <button
            onClick={() => setShowCart((s) => !s)}
            style={{ padding: "8px 12px" }}
          >
            {showCart ? "‚¨ÖÔ∏è Quay l·∫°i" : `üõí Gi·ªè (${cart.length})`}
          </button>
        </div>
      </div>

      {/* Trang ƒëƒÉng nh·∫≠p / ƒëƒÉng k√Ω */}
      {showLogin && (
        <LoginPage
          onClose={() => setShowLogin(false)}
          onSwitch={() => {
            setShowLogin(false);
            setShowRegister(true);
          }}
        />
      )}
      {showRegister && (
        <RegisterPage
          onClose={() => setShowRegister(false)}
          onSwitch={() => {
            setShowRegister(false);
            setShowLogin(true);
          }}
        />
      )}

      {/* Menu m√≥n ƒÉn */}
      {!showCart && !showLogin && !showRegister && (
        <>
          <div style={{ margin: "12px 0" }}>
            {categories.map((cat) => (
              <button
                key={cat}
                onClick={() => setFilter(cat)}
                style={{
                  marginRight: 8,
                  padding: "8px 12px",
                  borderRadius: 20,
                  background: filter === cat ? "#ff9800" : "#eee",
                }}
              >
                {cat}
              </button>
            ))}
          </div>

          <div className="grid">
            {filtered.map((food) => (
              <div key={food._id} style={{ width: "100%" }}>
                <FoodItem item={food} />
              </div>
            ))}
          </div>
        </>
      )}

      {/* Trang gi·ªè h√†ng */}
      {showCart && !showLogin && !showRegister && <CartPage />}
    </div>
  );
}

export default App;
