// App.js
import React, { useEffect, useState } from "react";
import { getFoods } from "./api";
import "./App.css";
import FoodItem from "./FoodItem";
import CartPage from "./CartPage";
import { useCart } from "./CartContext";
import { ToastContainer } from "react-toastify";

function App() {
  const [foods, setFoods] = useState([]);
  const [filter, setFilter] = useState("T·∫•t c·∫£");
  const [showCart, setShowCart] = useState(false);
  // useCart ch·ªâ ƒë·ªÉ tham kh·∫£o, nh∆∞ng FoodItem s·∫Ω g·ªçi addToCart tr·ª±c ti·∫øp
  const { cart } = useCart();

  useEffect(() => {
    async function fetchData() {
      const data = await getFoods();
      setFoods(data || []);
    }
    fetchData();
  }, []);

  const categories = ["T·∫•t c·∫£", ...new Set(foods.map(f => f.category || "Kh√°c"))];
  const filtered = filter === "T·∫•t c·∫£" ? foods : foods.filter(f => f.category === filter);

  return (
    <div className="container">
      <ToastContainer position="top-right" autoClose={1600} />
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h1>üçΩÔ∏è Foodie Express Menu</h1>
        <div>
          <button onClick={() => setShowCart(s => !s)} style={{ padding: "8px 12px" }}>
            {showCart ? "‚¨ÖÔ∏è Quay l·∫°i" : `üõí Gi·ªè (${cart.length})`}
          </button>
        </div>
      </div>

      {!showCart && (
        <>
          <div style={{ margin: "12px 0" }}>
            {categories.map(cat => (
              <button key={cat} onClick={() => setFilter(cat)} style={{ marginRight: 8, padding: "8px 12px", borderRadius: 20, background: filter === cat ? "#ff9800" : "#eee" }}>
                {cat}
              </button>
            ))}
          </div>

          <div className="grid">
            {filtered.map(food => (
              // **Ghi ch√∫ quan tr·ªçng**: FoodItem nh·∫≠n prop 'item'
              <div key={food._id} style={{ width: "100%" }}>
                <FoodItem item={food} />
              </div>
            ))}
          </div>
        </>
      )}

      {showCart && <CartPage />}
    </div>
  );
}

export default App;
