// CartContext.js
import React, { createContext, useContext, useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState(() => {
    try {
      const s = localStorage.getItem("cart");
      return s ? JSON.parse(s) : [];
    } catch {
      return [];
    }
  });

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // ðŸ›’ ThÃªm mÃ³n vÃ o giá» (há»— trá»£ sá»‘ lÆ°á»£ng)
  const addToCart = (food, quantity = 1) => {
    setCart((prev) => {
      const existing = prev.find((p) => p._id === food._id);
      if (existing) {
        const updated = prev.map((p) =>
          p._id === food._id ? { ...p, quantity: p.quantity + quantity } : p
        );
        toast.info(`TÄƒng sá»‘ lÆ°á»£ng ${food.name} lÃªn ${existing.quantity + quantity}`);
        return updated;
      } else {
        toast.success(`ÄÃ£ thÃªm ${food.name} x${quantity} vÃ o giá»`);
        return [...prev, { ...food, quantity }];
      }
    });
  };

  // âž• TÄƒng sá»‘ lÆ°á»£ng
  const increaseQuantity = (id) => {
    setCart((prev) =>
      prev.map((p) =>
        p._id === id ? { ...p, quantity: p.quantity + 1 } : p
      )
    );
  };

  // âž– Giáº£m sá»‘ lÆ°á»£ng
  const decreaseQuantity = (id) => {
    setCart((prev) =>
      prev
        .map((p) =>
          p._id === id && p.quantity > 1
            ? { ...p, quantity: p.quantity - 1 }
            : p
        )
        .filter((p) => p.quantity > 0)
    );
  };

  // ðŸ”¢ Cáº­p nháº­t sá»‘ lÆ°á»£ng thá»§ cÃ´ng (ngÆ°á»i dÃ¹ng gÃµ vÃ o input)
  const updateQuantity = (id, newQuantity) => {
    const quantity = Math.max(1, parseInt(newQuantity) || 1); // KhÃ´ng cho nhá» hÆ¡n 1
    setCart((prev) =>
      prev.map((p) =>
        p._id === id ? { ...p, quantity } : p
      )
    );
  };

  // âŒ XÃ³a má»™t mÃ³n
  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((p) => p._id !== id));
    toast.warn("ÄÃ£ xÃ³a mÃ³n khá»i giá» hÃ ng");
  };

  // ðŸ§¹ XÃ³a toÃ n bá»™
  const clearCart = () => {
    setCart([]);
    toast.info("ÄÃ£ xÃ³a toÃ n bá»™ giá» hÃ ng");
  };

  // ðŸ’° Tá»•ng giÃ¡
  const getTotalPrice = () =>
    cart.reduce((sum, i) => sum + i.price * i.quantity, 0);

  return (
    <CartContext.Provider
      value={{
        cart,
        addToCart,
        increaseQuantity,
        decreaseQuantity,
        updateQuantity, // ðŸ‘ˆ thÃªm hÃ m nÃ y
        removeFromCart,
        clearCart,
        getTotalPrice,
      }}
    >
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
