  const res = await fetch(`${API_BASE_URL}/api/auth/login`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(credentials),
  });

  if (!res.ok) {
    const err = await res.json().catch(() => ({}));
    throw new Error(err.message || "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i");
  }

  return res.json();
}

export async function socialLogin(idToken) {
  const res = await fetch("http://localhost:5000/api/auth/social-login", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ idToken }),
  });

  const data = await res.json();
  if (!res.ok) throw new Error(data.message || "Social login failed");
  return data;
}
