import React, { createContext, useContext, useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState(() => {
    const storedCart = localStorage.getItem("cart");
    return storedCart ? JSON.parse(storedCart) : [];
  });

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // ✅ Thêm món vào giỏ hàng với số lượng
  const addToCart = (food, quantity = 1) => {
    setCart((prevCart) => {
      const exist = prevCart.find((item) => item._id === food._id);
      if (exist) {
        const updated = prevCart.map((item) =>
          item._id === food._id
            ? { ...item, quantity: item.quantity + quantity }
            : item
        );
        toast.success(`Tăng số lượng ${food.name} lên ${exist.quantity + quantity}!`);
        return updated;
      } else {
        toast.success(`Đã thêm ${quantity} ${food.name} vào giỏ hàng!`);
        return [...prevCart, { ...food, quantity }];
      }
    });
  };

  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item._id !== id));
    toast.info("Đã xóa món khỏi giỏ hàng!");
  };

  const clearCart = () => setCart([]);

  const getTotalPrice = () =>
    cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <CartContext.Provider
      value={{ cart, addToCart, removeFromCart, clearCart, getTotalPrice }}
    >
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
