// RegisterPage.js
import React, { useState } from "react";
import { useAuth } from "./AuthContext";

export default function RegisterPage({ onSwitchToLogin }) {
  const { register } = useAuth();
  const [form, setForm] = useState({ name: "", email: "", password: "" });

  const handleSubmit = (e) => {
    e.preventDefault();
    register(form.name, form.email, form.password, () => window.location.href = "/");
  };

  return (
    <div style={wrapper}>
      <div style={card}>
        <h2>üìù ƒêƒÉng k√Ω t√†i kho·∫£n</h2>
        <form onSubmit={handleSubmit}>
          <input
            style={input}
            type="text"
            placeholder="H·ªç v√† t√™n"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
          <input
            style={input}
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
            required
          />
          <input
            style={input}
            type="password"
            placeholder="M·∫≠t kh·∫©u"
            value={form.password}
            onChange={(e) => setForm({ ...form, password: e.target.value })}
            required
          />
          <button style={btn} type="submit">ƒêƒÉng k√Ω</button>
        </form>
        <p style={{ marginTop: 12 }}>
          ƒê√£ c√≥ t√†i kho·∫£n?{" "}
          <button onClick={onSwitchToLogin} style={link}>ƒêƒÉng nh·∫≠p</button>
        </p>
      </div>
    </div>
  );
}

const wrapper = {
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  height: "100vh",
  background: "linear-gradient(135deg, #f8f9fa, #dfe6e9)",
};
const card = {
  width: 350,
  padding: 30,
  borderRadius: 12,
  background: "#fff",
  boxShadow: "0 2px 12px rgba(0,0,0,0.1)",
  textAlign: "center",
};
const input = {
  width: "100%",
  padding: 10,
  margin: "8px 0",
  borderRadius: 6,
  border: "1px solid #ccc",
};
const btn = {
  width: "100%",
  padding: 12,
  marginTop: 10,
  border: "none",
  borderRadius: 8,
  background: "#007bff",
  color: "#fff",
  cursor: "pointer",
  fontSize: 16,
  fontWeight: "bold",
};
const link = {
  background: "none",
  border: "none",
  color: "#007bff",
  cursor: "pointer",
};
