// src/FoodItem.js
import React, { useState } from "react";
import { useCart } from "./CartContext";
import { useAuth } from "./AuthContext";
import LoginRequiredModal from "./LoginRequiredModal";

export default function FoodItem({ food }) {
  const { addToCart } = useCart();
  const { user } = useAuth();

  const [openLoginModal, setOpenLoginModal] = useState(false);

  const handleBuy = () => {
    if (!user) {
      setOpenLoginModal(true);
      return;
    }
    addToCart(food, 1);
  };

  return (
    <>
      {/* ====== UI món ăn của bạn ====== */}
      <div style={styles.card}>
        <div style={styles.name}>{food?.name}</div>
        <div style={styles.price}>{Number(food?.price || 0).toLocaleString()} đ</div>

        <button style={styles.buyBtn} onClick={handleBuy}>
          Mua / Thêm giỏ
        </button>
      </div>

      {/* ====== Popup yêu cầu đăng nhập ====== */}
      <LoginRequiredModal
        open={openLoginModal}
        onClose={() => setOpenLoginModal(false)}
      />
    </>
  );
}

const styles = {
  card: {
    border: "1px solid #eee",
    borderRadius: 12,
    padding: 14,
    background: "#fff",
    boxShadow: "0 2px 10px rgba(0,0,0,0.06)",
  },
  name: { fontWeight: 800, marginBottom: 6 },
  price: { color: "#198754", fontWeight: 800, marginBottom: 10 },
  buyBtn: {
    width: "100%",
    padding: 10,
    border: "none",
    borderRadius: 10,
    background: "#198754",
    color: "#fff",
    fontWeight: 800,
    cursor: "pointer",
  },
};
