// src/FoodItem.js
import React, { useState } from "react";
import { useCart } from "./CartContext";
import { useAuth } from "./AuthContext";
import { toast } from "react-toastify";

export default function FoodItem({ item, onRequireLogin }) {
  const { addToCart } = useCart();
  const { user } = useAuth();

  // ch·ªëng l·ªói item undefined
  if (!item) return null;

  const name = item.name || "M√≥n ƒÉn";
  const price = Number(item.price || 0); // ‚úÖ l·∫•y ƒë√∫ng field t·ª´ API
  const image = item.image || "";
  const description = item.description || "";

  const [qty, setQty] = useState(1);

  const fmt = (v) => (Number(v) || 0).toLocaleString("vi-VN") + " ƒë";

  const handleBuy = () => {
    // ‚úÖ b·∫Øt bu·ªôc ƒëƒÉng nh·∫≠p
    if (!user) {
      toast.info("Vui l√≤ng ƒëƒÉng nh·∫≠p tr∆∞·ªõc khi mua.");
      onRequireLogin?.(); // m·ªü modal login n·∫øu App truy·ªÅn xu·ªëng
      return;
    }

    // ‚úÖ validate t·ªëi thi·ªÉu
    if (!item._id || !item.name) {
      toast.error("M√≥n ƒÉn kh√¥ng h·ª£p l·ªá (thi·∫øu d·ªØ li·ªáu).");
      return;
    }

    addToCart(item, qty);
  };

  return (
    <div style={card}>
      {image ? (
        <img src={image} alt={name} style={img} />
      ) : (
        <div style={imgPlaceholder}>üçú</div>
      )}

      <h3 style={{ margin: "10px 0 4px" }}>{name}</h3>
      {description && <p style={desc}>{description}</p>}

      <div style={priceText}>{fmt(price)}</div>

      <div style={row}>
        <button
          style={qtyBtn}
          onClick={() => setQty((q) => Math.max(1, q - 1))}
        >
          ‚àí
        </button>
        <input
          style={qtyInput}
          value={qty}
          onChange={(e) => setQty(Math.max(1, parseInt(e.target.value) || 1))}
        />
        <button style={qtyBtn} onClick={() => setQty((q) => q + 1)}>
          +
        </button>
      </div>

      <button style={buyBtn} onClick={handleBuy}>
        Mua / Th√™m gi·ªè
      </button>
    </div>
  );
}

const card = {
  width: "100%",
  background: "#fff",
  borderRadius: 14,
  padding: 14,
  boxShadow: "0 2px 10px rgba(0,0,0,0.08)",
  textAlign: "center",
};

const img = {
  width: "100%",
  height: 170,
  objectFit: "cover",
  borderRadius: 12,
};

const imgPlaceholder = {
  width: "100%",
  height: 170,
  borderRadius: 12,
  background: "#f1f3f5",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  fontSize: 40,
};

const desc = {
  margin: 0,
  color: "#666",
  fontSize: 13,
  minHeight: 34,
};

const priceText = {
  marginTop: 10,
  fontWeight: 800,
  fontSize: 18,
  color: "#148c3c",
};

const row = {
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  gap: 10,
  marginTop: 12,
};

const qtyBtn = {
  width: 36,
  height: 36,
  borderRadius: 10,
  border: "1px solid #ddd",
  background: "#fff",
  cursor: "pointer",
  fontSize: 18,
  fontWeight: 800,
};

const qtyInput = {
  width: 46,
  height: 36,
  textAlign: "center",
  borderRadius: 10,
  border: "1px solid #ddd",
  outline: "none",
  fontWeight: 700,
};

const buyBtn = {
  marginTop: 12,
  width: "100%",
  background: "#148c3c",
  color: "#fff",
  border: "none",
  borderRadius: 12,
  padding: "12px 12px",
  cursor: "pointer",
  fontWeight: 800,
  fontSize: 16,
};
