import React from "react";
import { useContext } from "react";
import { CartContext } from "./CartContext";

function CartPage() {
  const {
    cart,
    removeFromCart,
    updateQuantity,
    getTotalPrice,
    setCart,
  } = useContext(CartContext);

  const clearCart = () => {
    setCart([]);
    localStorage.removeItem("cart");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üõí Gi·ªè h√†ng c·ªßa b·∫°n</h2>

      {cart.length === 0 ? (
        <p>Ch∆∞a c√≥ m√≥n n√†o trong gi·ªè.</p>
      ) : (
        <>
          <ul>
            {cart.map((item) => (
              <li key={item.id} style={{ marginBottom: 10 }}>
                <strong>{item.name}</strong> - {item.price.toLocaleString()}‚Ç´ x{" "}
                {item.quantity}
                <div style={{ display: "inline-block", marginLeft: 10 }}>
                  <button
                    onClick={() => updateQuantity(item.id, -1)}
                    style={{
                      marginRight: 5,
                      padding: "2px 6px",
                      borderRadius: 4,
                    }}
                  >
                    ‚ûñ
                  </button>
                  <button
                    onClick={() => updateQuantity(item.id, +1)}
                    style={{
                      padding: "2px 6px",
                      borderRadius: 4,
                    }}
                  >
                    ‚ûï
                  </button>
                  <button
                    onClick={() => removeFromCart(item.id)}
                    style={{
                      marginLeft: 10,
                      color: "white",
                      background: "red",
                      border: "none",
                      borderRadius: 4,
                      padding: "3px 8px",
                      cursor: "pointer",
                    }}
                  >
                    ‚ùå X√≥a
                  </button>
                </div>
              </li>
            ))}
          </ul>

          <h3>
            üí∞ T·ªïng c·ªông:{" "}
            <span style={{ color: "green" }}>
              {getTotalPrice().toLocaleString()}‚Ç´
            </span>
          </h3>

          <button
            onClick={clearCart}
            style={{
              backgroundColor: "#555",
              color: "white",
              border: "none",
              padding: "6px 12px",
              borderRadius: 5,
              cursor: "pointer",
            }}
          >
            üßπ X√≥a gi·ªè h√†ng
          </button>
        </>
      )}
    </div>
  );
}

export default CartPage;
