// src/AddressBook.js
import React, { useEffect, useState } from "react";
import { createAddress, deleteAddress, getAddresses, updateAddress } from "./api";

const empty = {
  label: "Nh√†",
  recipientName: "",
  phone: "",
  line1: "",
  ward: "",
  district: "",
  city: "TP.HCM",
  note: "",
  isDefault: false,
};

export default function AddressBook() {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState(empty);
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(true);

  const reload = async () => {
    setLoading(true);
    try {
      const data = await getAddresses();
      setItems(data.items || []);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    reload();
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!form.line1.trim()) return alert("Vui l√≤ng nh·∫≠p ƒë·ªãa ch·ªâ (S·ªë nh√†/ƒê∆∞·ªùng)");

    if (editingId) {
      await updateAddress(editingId, form);
    } else {
      await createAddress(form);
    }

    setForm(empty);
    setEditingId(null);
    await reload();
  };

  const onEdit = (a) => {
    setEditingId(a._id);
    setForm({
      label: a.label || "Nh√†",
      recipientName: a.recipientName || "",
      phone: a.phone || "",
      line1: a.line1 || "",
      ward: a.ward || "",
      district: a.district || "",
      city: a.city || "TP.HCM",
      note: a.note || "",
      isDefault: !!a.isDefault,
    });
  };

  const onDelete = async (id) => {
    if (!window.confirm("X√≥a ƒë·ªãa ch·ªâ n√†y?")) return;
    await deleteAddress(id);
    await reload();
  };

  const makeDefault = async (a) => {
    await updateAddress(a._id, { ...a, isDefault: true });
    await reload();
  };

  return (
    <div className="fe-summaryCard">
      <div style={{ fontWeight: 900, fontSize: 16 }}>üè† ƒê·ªãa ch·ªâ giao h√†ng</div>
      <div style={{ color: "#667085", fontSize: 13, marginTop: 6 }}>Th√™m nhi·ªÅu ƒë·ªãa ch·ªâ v√† ch·ªçn m·∫∑c ƒë·ªãnh khi ƒë·∫∑t h√†ng.</div>

      <div style={{ display: "grid", gridTemplateColumns: "1fr", gap: 10, marginTop: 12 }}>
        {loading ? (
          <div style={{ color: "#667085" }}>ƒêang t·∫£i‚Ä¶</div>
        ) : items.length === 0 ? (
          <div style={{ color: "#667085" }}>Ch∆∞a c√≥ ƒë·ªãa ch·ªâ. Th√™m ·ªü form b√™n d∆∞·ªõi.</div>
        ) : (
          items.map((a) => (
            <div key={a._id} style={{ border: "1px solid #eef2f6", borderRadius: 14, padding: 12 }}>
              <div style={{ display: "flex", justifyContent: "space-between", gap: 10, alignItems: "flex-start" }}>
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: 900 }}>
                    {a.label || "ƒê·ªãa ch·ªâ"} {a.isDefault ? <span style={{ color: "#16a34a" }}>(M·∫∑c ƒë·ªãnh)</span> : null}
                  </div>
                  <div style={{ color: "#111", marginTop: 4 }}>
                    {a.line1}
                    {a.ward ? `, ${a.ward}` : ""}
                    {a.district ? `, ${a.district}` : ""}
                    {a.city ? `, ${a.city}` : ""}
                  </div>
                  <div style={{ color: "#667085", fontSize: 13, marginTop: 4 }}>
                    {a.recipientName ? `üë§ ${a.recipientName}` : ""} {a.phone ? ` ‚Ä¢ üìû ${a.phone}` : ""}
                  </div>
                  {a.note ? <div style={{ color: "#667085", fontSize: 13, marginTop: 4 }}>üìù {a.note}</div> : null}
                </div>

                <div style={{ display: "flex", gap: 8, flexWrap: "wrap", justifyContent: "flex-end" }}>
                  {!a.isDefault && (
                    <button className="fe-pill" onClick={() => makeDefault(a)}>
                      ƒê·∫∑t m·∫∑c ƒë·ªãnh
                    </button>
                  )}
                  <button className="fe-pill" onClick={() => onEdit(a)}>
                    S·ª≠a
                  </button>
                  <button className="fe-pill" style={{ borderColor: "#ffd1d1", background: "#fff5f5", color: "#b42318" }} onClick={() => onDelete(a._id)}>
                    X√≥a
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      <form onSubmit={onSubmit} style={{ marginTop: 14, borderTop: "1px solid #eee", paddingTop: 12, display: "grid", gap: 8 }}>
        <div style={{ fontWeight: 900 }}>{editingId ? "‚úèÔ∏è S·ª≠a ƒë·ªãa ch·ªâ" : "‚ûï Th√™m ƒë·ªãa ch·ªâ"}</div>

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 8 }}>
          <input className="fe-input" placeholder="Nh√£n (Nh√† / C√¥ng ty)" value={form.label} onChange={(e) => setForm({ ...form, label: e.target.value })} />
          <input className="fe-input" placeholder="T√™n ng∆∞·ªùi nh·∫≠n" value={form.recipientName} onChange={(e) => setForm({ ...form, recipientName: e.target.value })} />
        </div>

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 8 }}>
          <input className="fe-input" placeholder="SƒêT" value={form.phone} onChange={(e) => setForm({ ...form, phone: e.target.value })} />
          <input className="fe-input" placeholder="Th√†nh ph·ªë" value={form.city} onChange={(e) => setForm({ ...form, city: e.target.value })} />
        </div>

        <input className="fe-input" placeholder="ƒê·ªãa ch·ªâ (S·ªë nh√†, ƒë∆∞·ªùng...)" value={form.line1} onChange={(e) => setForm({ ...form, line1: e.target.value })} />

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 8 }}>
          <input className="fe-input" placeholder="Ph∆∞·ªùng/X√£" value={form.ward} onChange={(e) => setForm({ ...form, ward: e.target.value })} />
          <input className="fe-input" placeholder="Qu·∫≠n/Huy·ªán" value={form.district} onChange={(e) => setForm({ ...form, district: e.target.value })} />
        </div>

        <input className="fe-input" placeholder="Ghi ch√∫ (t√πy ch·ªçn)" value={form.note} onChange={(e) => setForm({ ...form, note: e.target.value })} />

        <label style={{ display: "flex", gap: 8, alignItems: "center", color: "#667085", fontSize: 13 }}>
          <input type="checkbox" checked={form.isDefault} onChange={(e) => setForm({ ...form, isDefault: e.target.checked })} />
          ƒê·∫∑t l√†m m·∫∑c ƒë·ªãnh
        </label>

        <div style={{ display: "flex", gap: 8 }}>
          <button className="fe-btn fe-btn-primary" type="submit">
            {editingId ? "L∆∞u" : "Th√™m"}
          </button>
          {editingId && (
            <button
              className="fe-btn"
              type="button"
              onClick={() => {
                setEditingId(null);
                setForm(empty);
              }}
            >
              H·ªßy
            </button>
          )}
        </div>
      </form>
    </div>
  );
}
