// src/AuthContext.js
import React, { createContext, useContext, useEffect, useState } from "react";
import { loginUser, registerUser, socialLogin } from "./api"; // ✅ dùng named exports

const AuthContext = createContext(null);

export const useAuth = () => useContext(AuthContext);

export default function AuthProvider({ children }) {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const u = localStorage.getItem("user");
    if (u) setUser(JSON.parse(u));
  }, []);

  const saveSession = (data) => {
    localStorage.setItem("token", data.token);
    localStorage.setItem("user", JSON.stringify(data.user));
    setUser(data.user);
  };

  // ✅ Email/password login (cũ)
  const login = async (email, password, cb) => {
    const data = await loginUser(email, password); // <- gọi đúng hàm
    saveSession(data);
    cb?.();
  };

  // ✅ Register (cũ)
  const register = async (name, email, password, cb) => {
    const data = await registerUser(name, email, password);
    saveSession(data);
    cb?.();
  };

  // ✅ Firebase social login -> backend phát JWT
  const social = async (idToken, cb) => {
    const data = await socialLogin(idToken);
    saveSession(data);
    cb?.();
  };

  const logout = () => {
    localStorage.clear();
    setUser(null);
    window.location.href = "/";
  };

  return (
    <AuthContext.Provider value={{ user, login, register, socialLogin: social, logout }}>
      {children}
    </AuthContext.Provider>
  );
}
