// src/OrderHistory.js
import React, { useEffect, useState } from "react";
import { demoAdvanceOrder, getMyOrders } from "./api";
import { useSocket } from './SocketContext';

export default function OrderHistory() {
  const { socket, connected } = useSocket();
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  const reload = async () => {
    setLoading(true);
    try {
      const data = await getMyOrders();
      setItems(Array.isArray(data) ? data : []);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    reload();
  }, []);

  // Realtime update: when staff updates order status
  useEffect(() => {
    if (!socket || !connected) return;
    const onUpdated = (payload) => {
      // simple approach: reload list
      reload();
    };
    socket.on('order:updated', onUpdated);
    return () => socket.off('order:updated', onUpdated);
    // eslint-disable-next-line
  }, [socket, connected]);

  const fmt = (v) => (Number(v) || 0).toLocaleString("vi-VN") + " ƒë";

  const statusLabel = (s) => {
    const m = {
      pending: "Ch·ªù x√°c nh·∫≠n",
      confirmed: "ƒê√£ x√°c nh·∫≠n",
      preparing: "ƒêang chu·∫©n b·ªã",
      delivering: "ƒêang giao",
      delivered: "ƒê√£ giao",
      cancelled: "ƒê√£ h·ªßy",
    };
    return m[s] || "ƒêang x·ª≠ l√Ω";
  };

  return (
    <div className="fe-summaryCard">
      <div style={{ fontWeight: 900, fontSize: 16 }}>üßæ L·ªãch s·ª≠ ƒë∆°n h√†ng</div>
      <div style={{ color: "#667085", fontSize: 13, marginTop: 6 }}>Xem l·∫°i c√°c ƒë∆°n b·∫°n ƒë√£ ƒë·∫∑t (demo).</div>

      <div style={{ marginTop: 12, display: "grid", gap: 10 }}>
        {loading ? (
          <div style={{ color: "#667085" }}>ƒêang t·∫£i‚Ä¶</div>
        ) : items.length === 0 ? (
          <div style={{ color: "#667085" }}>Ch∆∞a c√≥ ƒë∆°n h√†ng n√†o.</div>
        ) : (
          items.map((o) => (
            <div key={o._id} style={{ border: "1px solid #eef2f6", borderRadius: 14, padding: 12 }}>
              <div style={{ display: "flex", justifyContent: "space-between", gap: 10, flexWrap: "wrap" }}>
                <div>
                  <div style={{ fontWeight: 900 }}>ƒê∆°n #{String(o._id).slice(-6).toUpperCase()}</div>
                  <div style={{ color: "#667085", fontSize: 13, marginTop: 4 }}>
                    {new Date(o.createdAt).toLocaleString("vi-VN")} ‚Ä¢ {o.orderItems?.length || 0} m√≥n
                  </div>
                </div>
                <div style={{ textAlign: "right" }}>
                  <div style={{ fontWeight: 900 }}>{fmt(o.finalPrice || o.totalPrice)}</div>
                  {o.discountAmount ? <div style={{ color: "#16a34a", fontSize: 13 }}>- {fmt(o.discountAmount)} ({o.promoCode || "KM"})</div> : null}
                </div>
              </div>

              <div style={{ marginTop: 8, display: "flex", justifyContent: "space-between", gap: 10, flexWrap: "wrap", alignItems: "center" }}>
                <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
                  <span className="fe-pill" style={{ cursor: "default" }}>
                    üì¶ {statusLabel(o.status)}
                  </span>
                  <span className="fe-pill" style={{ cursor: "default" }}>
                    üöö Ship: {fmt(o.shippingFee || 0)}
                  </span>
                  <span className="fe-pill" style={{ cursor: "default" }}>
                    üí≥ {String(o.paymentMethod || "cod").toUpperCase()}
                  </span>
                </div>

                <button
                  className="fe-pill"
                  onClick={async () => {
                    try {
                      await demoAdvanceOrder(o._id);
                      await reload();
                    } catch (e) {
                      console.error(e);
                    }
                  }}
                  disabled={o.status === "delivered" || o.status === "cancelled"}
                >
                  ‚è≠Ô∏è C·∫≠p nh·∫≠t tr·∫°ng th√°i (demo)
                </button>
              </div>

              {o.shippingAddress?.line1 ? (
                <div style={{ marginTop: 8, color: "#667085", fontSize: 13 }}>
                  üìç {o.shippingAddress.line1}
                  {o.shippingAddress.district ? `, ${o.shippingAddress.district}` : ""}
                  {o.shippingAddress.city ? `, ${o.shippingAddress.city}` : ""}
                </div>
              ) : null}

              <div style={{ marginTop: 10, display: "grid", gap: 6 }}>
                {(o.orderItems || []).slice(0, 3).map((it, idx) => (
                  <div key={idx} style={{ display: "flex", justifyContent: "space-between", color: "#111" }}>
                    <span>
                      {it.name} √ó {it.qty}
                    </span>
                    <span style={{ color: "#667085" }}>{fmt(Number(it.price) * Number(it.qty))}</span>
                  </div>
                ))}
                {(o.orderItems || []).length > 3 ? <div style={{ color: "#667085", fontSize: 13 }}>‚Ä¶ v√† {(o.orderItems || []).length - 3} m√≥n kh√°c</div> : null}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
