import React, { createContext, useContext, useState } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cart, setCart] = useState([]);

  const addToCart = (food) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item._id === food._id);
      if (existingItem) {
        const updatedCart = prevCart.map((item) =>
          item._id === food._id
            ? { ...item, quantity: item.quantity + food.quantity }
            : item
        );
        toast.success(`Đã cập nhật số lượng cho ${food.name}`);
        return updatedCart;
      } else {
        toast.success(`${food.name} đã thêm vào giỏ hàng!`);
        return [...prevCart, { ...food }];
      }
    });
  };

  const removeFromCart = (id) => {
    setCart((prevCart) => prevCart.filter((item) => item._id !== id));
    toast.info("Đã xóa món khỏi giỏ hàng!");
  };

  const clearCart = () => {
    setCart([]);
    toast.info("Đã xóa toàn bộ giỏ hàng!");
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export const useCart = () => useContext(CartContext);
