import React, { useEffect, useState } from "react";
import { getFoods } from "./api";
import "./App.css";
import { useCart } from "./CartContext";
import CartPage from "./CartPage";

function App() {
  const [foods, setFoods] = useState([]);
  const [filter, setFilter] = useState("T·∫•t c·∫£");
  const [showCart, setShowCart] = useState(false);
  const { addToCart } = useCart();

  // ‚úÖ L∆∞u s·ªë l∆∞·ª£ng ri√™ng t·ª´ng m√≥n (theo ID)
  const [quantities, setQuantities] = useState({});

  useEffect(() => {
    async function fetchData() {
      const data = await getFoods();
      setFoods(data);

      // Kh·ªüi t·∫°o s·ªë l∆∞·ª£ng = 1 cho t·ª´ng m√≥n
      const initialQuantities = {};
      data.forEach((food) => (initialQuantities[food._id] = 1));
      setQuantities(initialQuantities);
    }
    fetchData();
  }, []);

  const categories = ["T·∫•t c·∫£", ...new Set(foods.map((f) => f.category || "Kh√°c"))];
  const filteredFoods =
    filter === "T·∫•t c·∫£" ? foods : foods.filter((f) => f.category === filter);

  // ‚úÖ Thay ƒë·ªïi s·ªë l∆∞·ª£ng ri√™ng t·ª´ng m√≥n
  const handleQuantityChange = (id, value) => {
    const qty = Math.max(1, parseInt(value) || 1);
    setQuantities((prev) => ({ ...prev, [id]: qty }));
  };

  // ‚úÖ Khi th√™m m√≥n v√†o gi·ªè
  const handleAddToCart = (food) => {
    const qty = quantities[food._id] || 1;
    addToCart({ ...food, quantity: qty });
  };

  return (
    <div className="container">
      <h1>üçΩÔ∏è Foodie Express Menu</h1>

      {/* N√∫t chuy·ªÉn gi·ªè h√†ng */}
      <div style={{ textAlign: "right", marginBottom: "10px" }}>
        <button className="cart-btn" onClick={() => setShowCart(!showCart)}>
          {showCart ? "‚¨ÖÔ∏è Quay l·∫°i menu" : "üõí Xem gi·ªè h√†ng"}
        </button>
      </div>

      {showCart ? (
        <CartPage />
      ) : (
        <>
          {/* B·ªô l·ªçc danh m·ª•c */}
          <div className="filter">
            {categories.map((cat) => (
              <button
                key={cat}
                className={filter === cat ? "active" : ""}
                onClick={() => setFilter(cat)}
              >
                {cat}
              </button>
            ))}
          </div>

          {/* Danh s√°ch m√≥n ƒÉn */}
          {foods.length === 0 ? (
            <p>Loading...</p>
          ) : (
            <div className="grid">
              {filteredFoods.map((food) => (
                <div key={food._id} className="card">
                  <img
                    src={
                      food.image ||
                      "https://via.placeholder.com/200x150?text=No+Image"
                    }
                    alt={food.name}
                  />
                  <h3>{food.name}</h3>
                  <p className="price">{food.price.toLocaleString()}‚Ç´</p>
                  <p className="category">{food.category}</p>

                  {/* √î ch·ªçn s·ªë l∆∞·ª£ng ri√™ng */}
                  <div className="quantity-box">
                    <label>S·ªë l∆∞·ª£ng: </label>
                    <input
                      type="number"
                      min="1"
                      value={quantities[food._id] || 1}
                      onChange={(e) =>
                        handleQuantityChange(food._id, e.target.value)
                      }
                    />
                  </div>

                  {/* N√∫t th√™m v√†o gi·ªè */}
                  <button
                    className="buy-btn"
                    onClick={() => handleAddToCart(food)}
                  >
                    üõç Th√™m v√†o gi·ªè
                  </button>
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
