// src/LoginPage.js
import React, { useState } from "react";
import { useAuth } from "./AuthContext";

// 笨 Google Firebase
import { auth, googleProvider } from "./firebase";
import { signInWithPopup } from "firebase/auth";

export default function LoginPage({ onSwitchToRegister }) {
  const { login, socialLogin } = useAuth();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // 笨 Login email/pass (cﾅｩ)
  const handleSubmit = (e) => {
    e.preventDefault();
    login(email, password, () => (window.location.href = "/"));
  };

  // 笨 Login Google (Firebase -> idToken -> backend -> JWT)
  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const idToken = await result.user.getIdToken();

      await socialLogin(idToken, () => (window.location.href = "/"));
    } catch (err) {
      console.error(err);
      alert("ﾄ斉ハg nh蘯ｭp Google th蘯･t b蘯｡i. Hﾃ｣y ki盻ノ tra Firebase Auth (Google provider).");
    }
  };

  return (
    <div style={wrapper}>
      <div style={card}>
        <h2>沐 ﾄ斉ハg nh蘯ｭp</h2>

        <form onSubmit={handleSubmit}>
          <input
            style={input}
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            style={input}
            type="password"
            placeholder="M蘯ｭt kh蘯ｩu"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button style={btn} type="submit">ﾄ斉ハg nh蘯ｭp</button>
        </form>

        {/* 笨 Nﾃｺt Google */}
        <button style={btnGoogle} onClick={handleGoogleLogin}>
          沐ｵ ﾄ斉ハg nh蘯ｭp b蘯ｱng Google
        </button>

        <p style={{ marginTop: 12 }}>
          Chﾆｰa cﾃｳ tﾃi kho蘯｣n?{" "}
          <button onClick={onSwitchToRegister} style={link}>ﾄ斉ハg kﾃｽ ngay</button>
        </p>
      </div>
    </div>
  );
}

const wrapper = {
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  height: "100vh",
  background: "linear-gradient(135deg, #f8f9fa, #dfe6e9)",
};
const card = {
  width: 350,
  padding: 30,
  borderRadius: 12,
  background: "#fff",
  boxShadow: "0 2px 12px rgba(0,0,0,0.1)",
  textAlign: "center",
};
const input = {
  width: "100%",
  padding: 10,
  margin: "8px 0",
  borderRadius: 6,
  border: "1px solid #ccc",
};
const btn = {
  width: "100%",
  padding: 12,
  marginTop: 10,
  border: "none",
  borderRadius: 8,
  background: "#28a745",
  color: "#fff",
  cursor: "pointer",
  fontSize: 16,
  fontWeight: "bold",
};
const btnGoogle = {
  width: "100%",
  padding: 12,
  marginTop: 10,
  borderRadius: 8,
  border: "1px solid #ddd",
  background: "#fff",
  cursor: "pointer",
  fontSize: 15,
  fontWeight: "bold",
};
const link = {
  background: "none",
  border: "none",
  color: "#007bff",
  cursor: "pointer",
};
