// AuthContext.js
import React, { createContext, useContext, useEffect, useState } from "react";
import API from "./untils/api"; // ✅ nếu bạn đang để API ở src/utils/api.js thì sửa thành "../utils/api"

const AuthContext = createContext(null);

export const useAuth = () => useContext(AuthContext);

export default function AuthProvider({ children }) {
  const [user, setUser] = useState(() => {
    const u = localStorage.getItem("user");
    return u ? JSON.parse(u) : null;
  });

  useEffect(() => {
    const u = localStorage.getItem("user");
    if (u) setUser(JSON.parse(u));
  }, []);

  const saveSession = (data) => {
    localStorage.setItem("token", data.token);
    localStorage.setItem("user", JSON.stringify(data.user));
    setUser(data.user);
  };

  // ✅ login email/pass (cũ)
  const login = async (email, password, cb) => {
    const { data } = await API.post("/auth/login", { email, password });
    saveSession(data);
    cb?.();
  };

  // ✅ register (cũ)
  const register = async (name, email, password, cb) => {
    const { data } = await API.post("/auth/register", { name, email, password });
    saveSession(data);
    cb?.();
  };

  // ✅ login Google/Facebook/Phone: nhận Firebase idToken -> backend phát JWT
  const socialLogin = async (idToken, cb) => {
    const { data } = await API.post("/auth/social-login", { idToken });
    saveSession(data);
    cb?.();
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    window.location.href = "/"; // tuỳ bạn
  };

  return (
    <AuthContext.Provider value={{ user, login, register, socialLogin, logout }}>
      {children}
    </AuthContext.Provider>
  );
}
