// LoginPage.js
import React, { useState } from "react";
import { useAuth } from "./AuthContext";

// ‚úÖ Firebase auth imports
import { auth, googleProvider, facebookProvider } from "./firebase";
import {
  signInWithPopup,
  RecaptchaVerifier,
  signInWithPhoneNumber,
} from "firebase/auth";

export default function LoginPage({ onSwitchToRegister }) {
  const { login, socialLogin } = useAuth();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Phone OTP
  const [phone, setPhone] = useState(""); // v√≠ d·ª•: +84901234567
  const [otp, setOtp] = useState("");
  const [otpStep, setOtpStep] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    login(email, password, () => (window.location.href = "/"));
  };

  // ‚úÖ Google login
  const handleGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const idToken = await result.user.getIdToken();
      await socialLogin(idToken, () => (window.location.href = "/"));
    } catch (e) {
      console.error(e);
      alert("Google login l·ªói (h√£y b·∫≠t Google provider trong Firebase Auth)");
    }
  };

  // ‚úÖ Facebook login
  const handleFacebook = async () => {
    try {
      const result = await signInWithPopup(auth, facebookProvider);
      const idToken = await result.user.getIdToken();
      await socialLogin(idToken, () => (window.location.href = "/"));
    } catch (e) {
      console.error(e);
      alert("Facebook login l·ªói (h√£y b·∫≠t Facebook provider + c·∫•u h√¨nh redirect URI)");
    }
  };

  // ‚úÖ Phone OTP
  const setupRecaptcha = () => {
    if (window.recaptchaVerifier) return;
    window.recaptchaVerifier = new RecaptchaVerifier(auth, "recaptcha-container", {
      size: "invisible",
    });
  };

  const sendOtp = async () => {
    try {
      setupRecaptcha();
      const confirmation = await signInWithPhoneNumber(auth, phone, window.recaptchaVerifier);
      window.confirmationResult = confirmation;
      setOtpStep(true);
      alert("ƒê√£ g·ª≠i OTP v·ªÅ SMS");
    } catch (e) {
      console.error(e);
      alert("G·ª≠i OTP l·ªói (ki·ªÉm tra Phone provider + authorized domain localhost)");
    }
  };

  const confirmOtp = async () => {
    try {
      const cred = await window.confirmationResult.confirm(otp);
      const idToken = await cred.user.getIdToken();
      await socialLogin(idToken, () => (window.location.href = "/"));
    } catch (e) {
      console.error(e);
      alert("OTP kh√¥ng ƒë√∫ng ho·∫∑c h·∫øt h·∫°n");
    }
  };

  return (
    <div style={wrapper}>
      <div style={card}>
        <h2>üîê ƒêƒÉng nh·∫≠p</h2>

        {/* Email/Password */}
        <form onSubmit={handleSubmit}>
          <input
            style={input}
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            style={input}
            type="password"
            placeholder="M·∫≠t kh·∫©u"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button style={btn} type="submit">ƒêƒÉng nh·∫≠p</button>
        </form>

        {/* Social */}
        <div style={{ marginTop: 12 }}>
          <button style={btnOutline} onClick={handleGoogle}>üîµ ƒêƒÉng nh·∫≠p Google</button>
          <button style={btnOutlineFb} onClick={handleFacebook}>üü¶ ƒêƒÉng nh·∫≠p Facebook</button>
        </div>

        {/* Phone OTP */}
        <div style={{ marginTop: 14, textAlign: "left" }}>
          <div style={{ fontWeight: 600, marginBottom: 6 }}>ƒêƒÉng nh·∫≠p b·∫±ng SƒêT</div>

          {!otpStep ? (
            <>
              <input
                style={input}
                type="text"
                placeholder="SƒêT d·∫°ng +84... (vd: +84901234567)"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
              <button style={btnPhone} onClick={sendOtp}>G·ª≠i OTP</button>
            </>
          ) : (
            <>
              <input
                style={input}
                type="text"
                placeholder="Nh·∫≠p OTP"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
              />
              <button style={btnPhone} onClick={confirmOtp}>X√°c nh·∫≠n OTP</button>
              <button style={linkBtn} onClick={() => setOtpStep(false)}>ƒê·ªïi s·ªë / G·ª≠i l·∫°i</button>
            </>
          )}

          {/* Recaptcha container */}
          <div id="recaptcha-container"></div>
        </div>

        <p style={{ marginTop: 14 }}>
          Ch∆∞a c√≥ t√†i kho·∫£n?{" "}
          <button onClick={onSwitchToRegister} style={link}>ƒêƒÉng k√Ω ngay</button>
        </p>
      </div>
    </div>
  );
}

const wrapper = {
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  height: "100vh",
  background: "linear-gradient(135deg, #f8f9fa, #dfe6e9)",
};
const card = {
  width: 380,
  padding: 30,
  borderRadius: 12,
  background: "#fff",
  boxShadow: "0 2px 12px rgba(0,0,0,0.1)",
  textAlign: "center",
};
const input = {
  width: "100%",
  padding: 10,
  margin: "8px 0",
  borderRadius: 6,
  border: "1px solid #ccc",
};
const btn = {
  width: "100%",
  padding: 12,
  marginTop: 10,
  border: "none",
  borderRadius: 8,
  background: "#28a745",
  color: "#fff",
  cursor: "pointer",
  fontSize: 16,
  fontWeight: "bold",
};
const btnOutline = {
  width: "100%",
  padding: 12,
  marginTop: 8,
  borderRadius: 8,
  border: "1px solid #333",
  background: "#fff",
  cursor: "pointer",
  fontWeight: "bold",
};
const btnOutlineFb = {
  width: "100%",
  padding: 12,
  marginTop: 8,
  borderRadius: 8,
  border: "1px solid #1877F2",
  background: "#fff",
  cursor: "pointer",
  fontWeight: "bold",
};
const btnPhone = {
  width: "100%",
  padding: 12,
  marginTop: 6,
  border: "none",
  borderRadius: 8,
  background: "#0d6efd",
  color: "#fff",
  cursor: "pointer",
  fontWeight: "bold",
};
const link = {
  background: "none",
  border: "none",
  color: "#007bff",
  cursor: "pointer",
};
const linkBtn = {
  background: "none",
  border: "none",
  color: "#007bff",
  cursor: "pointer",
  marginTop: 6,
};
