// LoginPage.js
import React, { useState } from "react";
import { useAuth } from "./AuthContext";

export default function LoginPage({ onSwitchToRegister }) {
  const { login } = useAuth();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    login(email, password);
  };

  return (
    <div style={{ maxWidth: 400, margin: "40px auto" }}>
      <h2>üîê ƒêƒÉng nh·∫≠p</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={inputStyle}
        />
        <input
          type="password"
          placeholder="M·∫≠t kh·∫©u"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={inputStyle}
        />
        <button type="submit" style={btnStyle}>ƒêƒÉng nh·∫≠p</button>
      </form>
      <p>
        Ch∆∞a c√≥ t√†i kho·∫£n?{" "}
        <button onClick={onSwitchToRegister} style={linkStyle}>ƒêƒÉng k√Ω ngay</button>
      </p>
    </div>
  );
}

const inputStyle = {
  width: "100%",
  padding: "8px",
  margin: "6px 0",
  borderRadius: 6,
  border: "1px solid #ccc",
};

const btnStyle = {
  width: "100%",
  padding: "10px",
  marginTop: 10,
  border: "none",
  background: "#28a745",
  color: "white",
  borderRadius: 6,
  cursor: "pointer",
};

const linkStyle = {
  color: "#007bff",
  background: "none",
  border: "none",
  cursor: "pointer",
};
